import React from 'react';
import { Button, Card, Col, Container, Image, Row } from 'react-bootstrap';
import ratingImg from '../assets/ratingImg.png';
import noImage from '../assets/no-image.png';

const DevicePage = ({ device }) => {
  // Видалення невикористовуваних змінних
  // const ratingImg = '../assets/ratingImg.png'; // Вже імпортовано
  // const noImage = '../assets/no-image.png'; // Вже імпортовано

  // Умовна логіка або зображення-заповнювач для відсутнього зображення пристрою
  const deviceImage = device.img ? `${process.env.REACT_APP_API_URL}${device.img}` : noImage;

  // Заповнювач для зображення рейтингу до його визначення
  const ratingStar = <Image width={240} height={240} src={ratingImg} alt="Зірка рейтингу" />; // Заповнювач

  return (
    <Container className="mt-3">
      <Row>
        <Col md={4}>
          <Image width={300} height={300} src={deviceImage} />
        </Col>
        <Col md={4}>
          <Row className="d-flex flex-column align-items-center">
            <h2>{device.name}</h2>
            <div
              className="d-flex align-items-center justify-content-center"
              style={{
                background: `url(${ratingStar.src}) no-repeat center center`,
                width: 240,
                height: 240,
                backgroundSize: 'cover',
                fontSize: 64,
              }}
            >
              {device.rating}
            </div>
          </Row>
        </Col>
        <Col md={4}>
          <Card
            className="d-flex flex-column align-items-center justify-content-around"
            style={{ width: 300, height: 300, fontSize: 32, border: '5px solid lightgray' }}
          >
            <h3>Від: {device.price} грн.</h3>
            <Button variant="outline-dark">Додати в корзину</Button>
          </Card>
        </Col>
      </Row>
      <Row className="d-flex flex-column m-3">
        <h1>Характеристики</h1>
        {device.description?.map((info, index) => ( // Оператор необов'язкового ланцюжка для безпеки
          <Row key={info.id} style={{ background: index % 2 === 0 ? 'lightgray' : 'transparent', padding: 10 }}>
            {info.title}: {info.description}
          </Row>
        ))}
      </Row>
    </Container>
  );
};

export default DevicePage
